<script>
jQuery(document).ready(function($) {
  // Toggle the FAQ sections on click
  $('.elementor-toggle-item .elementor-tab-title').click(function() {
    var $this = $(this);
    var $toggleContent = $this.next();

    // Toggle the current FAQ item's content visibility
    $toggleContent.slideToggle(200);

    // Change the aria-expanded attribute accordingly
    var isExpanded = $this.attr('aria-expanded') === 'true';
    $this.attr('aria-expanded', !isExpanded);

    // Toggle the icon class to indicate open/close state
    $this.find('.elementor-toggle-icon').toggleClass('elementor-toggle-icon-right elementor-toggle-icon-down');
  });
});
</script>



